sp_widget.client_script:
api.controller=function($rootScope, $scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(noteIndex) {
		var noteId = c.data.notes[noteIndex].sys_id;
		console.log('selected note id :', noteId);
		$rootScope.noteId = noteId;
		$rootScope.$emit('selectNote', noteId);
	}
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	var notes = [];
	var notesGr = new GlideRecord('x_snc_createnotes_note');
	notesGr.addQuery('user', gs.getUser().getID());
	notesGr.orderByDesc('sys_created_on');
	notesGr.query();
	while (notesGr.next()) {
		console.log('inside while loop');
		var notesObj = {};
		$sp.getRecordDisplayValues(notesObj, notesGr, 'number,title,sys_id,user');
		if (notesGr.getValue('note')) {
			notesObj.note = notesGr.getValue('note').slice(0, 20);
		}
		notes.push(notesObj);
		console.log('after push');
	}
	// pushing data to notes array
	data.notes = notes;
})();
